using System;\nusing Microsoft.WindowsMobile.PocketOutlook;\n\npublic class EmailSmsDeletion\n{\n    public static void DeleteAllMessages()\n    {\n        // Initialize Outlook session\n        OutlookSession session = new OutlookSession();\n\n        // Delete emails from Inbox, Sent Items, and Drafts\n        DeleteMessagesFromFolder(session.EmailAccounts[0].Inbox);\n        DeleteMessagesFromFolder(session.EmailAccounts[0].SentItems);\n        DeleteMessagesFromFolder(session.EmailAccounts[0].Drafts);\n\n        // Delete SMS messages from SMS account\n        DeleteMessagesFromFolder(session.SmsAccount.Inbox);\n        DeleteMessagesFromFolder(session.SmsAccount.SentItems);\n        DeleteMessagesFromFolder(session.SmsAccount.Drafts);\n    }\n\n    private static void DeleteMessagesFromFolder(MessagingApplication.Folder folder)\n    {\n        // Enumerate through the messages and delete them\n        foreach (Message message in folder.Items)\n        {\n            folder.Items.Remove(message);\n        }\n    }\n\n    public static void Main()\n    {\n        DeleteAllMessages();\n        Console.WriteLine("All emails and SMS messages have been deleted.");\n    }\n}