using System;\nusing System.Net;\nusing System.IO;\n\npublic class ProxyExample\n{\n    public static void Main()\n    {\n        // Define the proxy server address, username, and password\n        string proxyAddress = "http://your.proxy.server:port";\n        string proxyUsername = "yourUsername";\n        string proxyPassword = "yourPassword";\n\n        // Create a WebProxy object\n        WebProxy proxy = new WebProxy(proxyAddress);\n\n        // If the proxy requires authentication, set the credentials\n        proxy.Credentials = new NetworkCredential(proxyUsername, proxyPassword);\n\n        // Create a WebRequest object and set the proxy\n        WebRequest request = WebRequest.Create("http://www.example.com");\n        request.Proxy = proxy;\n\n        // Get the response from the server\n        using (WebResponse response = request.GetResponse())\n        {\n            using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n            {\n                string responseText = reader.ReadToEnd();\n                Console.WriteLine(responseText);\n            }\n        }\n    }\n}