using System;\nusing Microsoft.Diagnostics.Runtime;\n\npublic class Program\n{\n    public class MyClass\n    {\n        public int IntValue;\n        public double DoubleValue;\n        public string StringValue;\n    }\n\n    public static void Main()\n    {\n        MyClass myObject = new MyClass { IntValue = 42, DoubleValue = 3.14, StringValue = "Hello" };\n        long size = GetObjectSize(myObject);\n        Console.WriteLine("Size of MyClass object: " + size);\n    }\n\n    public static long GetObjectSize(object obj)\n    {\n        using (var dataTarget = DataTarget.CreateSnapshotAndAttach())\n        {\n            ClrRuntime runtime = dataTarget.ClrVersions[0].CreateRuntime();\n            ClrHeap heap = runtime.Heap;\n            ClrType type = heap.GetObjectType(obj);\n            return type.GetSize(obj);\n        }\n    }\n}