<%@ Page Title="" Language="C#" MasterPageFile="~/Views/Shared/Site.Master" Inherits="System.Web.Mvc.ViewPage<NonStockSystem.Models.AdminViewModel>" %>\n   <%@ Import Namespace="NonStockSystem.Models" %>\n   <asp:Content ID="Content1" ContentPlaceHolderID="MainContent" runat="server">\n\n   <ul>\n   <% foreach (var c in Model.Categories) { %>\n       <li><a href="#<%=c.Name %>"><%=c.Name %></a></li>\n   <% } %>\n   </ul>\n\n   <hr />\n\n   <% foreach (var c in Model.Categories) { %>\n       <h2><a name="<%=c.Name %>"><%=c.Name %></a></h2>\n       <% foreach (var p in Model.Products.Where(p => p.CategoryID == c.ID)) { %>\n           <%=p.Name %> (<a href="/Admin/Edit/<%=p.ID %>">Edit</a> - <a href="/Admin/Delete/<%=p.ID %>">Delete</a>)<br />\n       <% } %>\n   <% } %>\n\n   </asp:Content>