private void textBox1_TextChanged(object sender, EventArgs e)\n{\n    string yourSentence = textBox1.Text.ToLower().Trim();\n\n    int counta = yourSentence.Count(c => c == 'a');\n    int counte = yourSentence.Count(c => c == 'e');\n    int counti = yourSentence.Count(c => c == 'i');\n    int counto = yourSentence.Count(c => c == 'o');\n    int countu = yourSentence.Count(c => c == 'u');\n\n    int totalVowels = counta + counte + counti + counto + countu;\n\n    listBox1.Items.Clear();\n    listBox1.Items.Add($"There are {counta} a's in the sentence");\n    listBox1.Items.Add($"There are {counte} e's in the sentence");\n    listBox1.Items.Add($"There are {counti} i's in the sentence");\n    listBox1.Items.Add($"There are {counto} o's in the sentence");\n    listBox1.Items.Add($"There are {countu} u's in the sentence");\n    listBox1.Items.Add($"All in all there are {totalVowels} vowels in the sentence");\n}\n\n// Ensure you subscribe to the TextChanged event for textBox1\nprivate void Form1_Load(object sender, EventArgs e)\n{\n    textBox1.TextChanged += new EventHandler(textBox1_TextChanged);\n}