using System;\nusing System.Data.SqlClient;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string query = "SELECT * FROM MyTable";\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand(query, conn);\n            cmd.CommandTimeout = 60; // Set the timeout in seconds\n\n            try\n            {\n                conn.Open();\n                var reader = cmd.ExecuteReader();\n                while (reader.Read())\n                {\n                    // Process the data\n                }\n                Console.WriteLine("Statement completed in less than 60 seconds");\n            }\n            catch (SqlException ex)\n            {\n                if (ex.Number == -2) // SQL Server error code for timeout\n                {\n                    Console.WriteLine("Statement timed out after 60 seconds");\n                }\n                else\n                {\n                    Console.WriteLine("SQL Error: " + ex.Message);\n                }\n            }\n        }\n    }\n}