// Assuming you have a DataSet, DataTable, BindingSource and DataGridView already set up.\nDataSet dataSet = new DataSet();\nDataTable dataTable = new DataTable();\nBindingSource bindingSource = new BindingSource();\nDataGridView dataGridView = new DataGridView();\n\n// Set up DataTable and DataSet (this is just an example setup)\ndataSet.Tables.Add(dataTable);\nbindingSource.DataSource = dataSet.Tables[0];\ndataGridView.DataSource = bindingSource;\n\n// Method to check if there are any changes\nprivate bool HasChanges()\n{\n    return dataSet.HasChanges();\n}\n\n// Example method to show a confirmation dialog before saving changes\nprivate void SaveChanges()\n{\n    if (HasChanges())\n    {\n        DialogResult result = MessageBox.Show("There are unsaved changes. Do you want to save them?", "Confirm Save", MessageBoxButtons.YesNo);\n        if (result == DialogResult.Yes)\n        {\n            // Code to save changes to the database\n            // Example: dataAdapter.Update(dataTable);\n        }\n    }\n}