// View.aspx\n<% using (Ajax.BeginForm("Action", "Controller", \n           new AjaxOptions { UpdateTargetId = "MyControl",  })) \n{ %>\n    <span id="MyControl">\n       <% Html.RenderPartial("MyControl"); %>\n    </span>\n    <input type="hidden" id="clickedButton" name="clickedButton" value="" />\n<% } %>\n\n// MyControl.ascx\n<%@ Control Language="C#" Inherits="System.Web.Mvc.ViewUserControl" %>\n<input name="prev" type="submit" value="prev" onclick="setClickedButton('prev')" />\n<input name="next" type="submit" value="next" onclick="setClickedButton('next')" />\n//...\n\n<script type="text/javascript">\n    function setClickedButton(buttonName) {\n        document.getElementById('clickedButton').value = buttonName;\n    }\n</script>