using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming you have retrieved the DateTime from MSSQL using GetUTCDate()\n        DateTime mssqlUtcDateTime = DateTime.UtcNow; // Example value, replace with actual MSSQL retrieval\n\n        // Convert DateTime to Unix timestamp\n        DateTimeOffset dateTimeOffset = new DateTimeOffset(mssqlUtcDateTime);\n        long unixTimestamp = dateTimeOffset.ToUnixTimeSeconds();\n\n        // Output the Unix timestamp\n        Console.WriteLine(unixTimestamp);\n    }\n}