using System;\nusing System.IO;\nusing System.Web.Mvc;\n\npublic class ScreenshotController : Controller\n{\n    [HttpPost]\n    public ActionResult SaveScreenshot(ScreenshotModel model)\n    {\n        if (model.Image.StartsWith("data:image/png;base64,"))\n        {\n            var base64Data = model.Image.Substring("data:image/png;base64,".Length);\n            var bytes = Convert.FromBase64String(base64Data);\n            var filePath = Server.MapPath("~/Screenshots/screenshot.png");\n            System.IO.File.WriteAllBytes(filePath, bytes);\n            return Json(new { success = true, filePath = filePath });\n        }\n        return Json(new { success = false });\n    }\n}\n\npublic class ScreenshotModel\n{\n    public string Image { get; set; }\n}