public ActionResult DeleteUser(int userId)\n{\n    try\n    {\n        // Your logic to delete the user\n        if (/* deletion successful */)\n        {\n            return Content("ok");\n        }\n        else\n        {\n            // Return a 400 Bad Request status code with a custom message\n            Response.StatusCode = 400;\n            return Content("Error: Unable to delete user");\n        }\n    }\n    catch (Exception ex)\n    {\n        // Log the exception\n        // Return a 500 Internal Server Error status code with the exception message\n        Response.StatusCode = 500;\n        return Content("Error: " + ex.Message);\n    }\n}