protected void Application_BeginRequest(Object sender, EventArgs e)\n{\n    HttpContext context = HttpContext.Current;\n    if (context != null && context.Session != null)\n    {\n        // Log session ID and request details here\n        var sessionId = context.Session.SessionID;\n        var requestId = Guid.NewGuid().ToString();\n        LogSessionDetails(requestId, sessionId, context.Request.Url.ToString());\n    }\n}\n\nprivate void LogSessionDetails(string requestId, string sessionId, string requestUrl)\n{\n    // Implement your logging here, e.g., write to a file or a database\n    System.IO.File.AppendAllText("C:\\SessionLogs.txt", $"{DateTime.Now}: Request ID: {requestId}, Session ID: {sessionId}, URL: {requestUrl}\n");\n}