using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        IDictionary<string, string> dictionary = new Dictionary<string, string>\n        {\n            { "Item1", "" },\n            { "Item2", "" },\n            { "Item3", "" }\n        };\n        \n        List<string> words = new List<string> { "This", "is", "a", "list", "of", "words", "that", "need", "to", "be", "split", "into", "the", "dictionary", "items" };\n        \n        int maxLength = 50;\n        int currentItemIndex = 1;\n        string currentKey = "Item" + currentItemIndex;\n        \n        foreach (var word in words)\n        {\n            if (dictionary[currentKey].Length + word.Length + 1 > maxLength)\n            {\n                currentItemIndex++;\n                if (currentItemIndex > 3)\n                {\n                    Console.WriteLine("Exceeded the number of available items in the dictionary.");\n                    break;\n                }\n                currentKey = "Item" + currentItemIndex;\n            }\n            \n            if (dictionary[currentKey].Length > 0)\n            {\n                dictionary[currentKey] += " ";\n            }\n            \n            dictionary[currentKey] += word;\n        }\n        \n        // Output the dictionary items\n        foreach (var kvp in dictionary)\n        {\n            Console.WriteLine($"{kvp.Key}: {kvp.Value}");\n        }\n    }\n}