using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Child\n{\n    public int ID { get; set; }\n    public int Amount { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var parentCollection = new List<List<Child>>\n        {\n            new List<Child>\n            {\n                new Child { ID = 1, Amount = 4 },\n                new Child { ID = 2, Amount = 7 }\n            },\n            new List<Child>\n            {\n                new Child { ID = 1, Amount = 2 },\n                new Child { ID = 2, Amount = 4 }\n            },\n            new List<Child>\n            {\n                new Child { ID = 1, Amount = 5 },\n                new Child { ID = 2, Amount = 3 }\n            }\n        };\n\n        var result = parentCollection\n            .SelectMany(childCollection => childCollection)\n            .GroupBy(child => child.ID)\n            .Select(group => new \n            {\n                ID = group.Key,\n                Avg = group.Average(child => child.Amount)\n            });\n\n        foreach (var item in result)\n        {\n            Console.WriteLine($"ID: {item.ID}, Avg: {item.Avg:F2}");\n        }\n    }\n}