using System;\nusing Twilio;\nusing Twilio.Rest.Lookups.V1;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Your Account SID and Auth Token from twilio.com/console\n        const string accountSid = "your_account_sid";\n        const string authToken = "your_auth_token";\n\n        TwilioClient.Init(accountSid, authToken);\n\n        try\n        {\n            var phoneNumber = PhoneNumberResource.Fetch(\n                pathPhoneNumber: new Twilio.Types.PhoneNumber("+1234567890"),\n                type: new List<string> { "carrier" }\n            );\n\n            Console.WriteLine($"Phone Number: {phoneNumber.PhoneNumber}");\n            Console.WriteLine($"Carrier: {phoneNumber.Carrier["name"]}");\n            Console.WriteLine($"Country Code: {phoneNumber.CountryCode}");\n        }\n        catch (Twilio.Exceptions.ApiException e)\n        {\n            Console.WriteLine($"Error: {e.Message}");\n        }\n    }\n}