protected override void OnPreRender(EventArgs e)\n{\n    base.OnPreRender(e);\n\n    // Generate a unique identifier for the UserControl\n    string controlId = this.ClientID;\n\n    // JavaScript function to initialize the multi-select\n    string script = $@"\n        Sys.Application.add_load(function() {{\n            $('#{controlId} select').multiselect();\n        }});";\n\n    // Register the script\n    ScriptManager.RegisterStartupScript(this, this.GetType(), controlId + "_initScript", script, true);\n}