using System;\nusing System.Diagnostics;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string currentUsername = Environment.UserName;\n        foreach (Process process in Process.GetProcesses())\n        {\n            string processOwner = GetProcessOwner(process.Id);\n            if (processOwner == currentUsername)\n            {\n                Console.WriteLine($"Process: {process.ProcessName}, ID: {process.Id}, Owner: {processOwner}");\n            }\n        }\n    }\n\n    static string GetProcessOwner(int processId)\n    {\n        string query = $"SELECT * FROM Win32_Process WHERE ProcessId = {processId}";\n        using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(query))\n        {\n            foreach (ManagementObject obj in searcher.Get())\n            {\n                object[] outParameters = new object[2];\n                obj.InvokeMethod("GetOwner", outParameters);\n                return $"{outParameters[1]}\\{outParameters[0]}"; // Domain\Username\n            }\n        }\n        return "NO OWNER";\n    }\n}