private void dataGridView1_SortCompare(object sender, DataGridViewSortCompareEventArgs e)\n{\n    // Assuming the custom column index is 1\n    if (e.Column.Index == 1)\n    {\n        string description1 = e.CellValue1?.ToString();\n        string description2 = e.CellValue2?.ToString();\n\n        e.SortResult = string.Compare(description1, description2);\n\n        e.Handled = true; // Indicate that the sort operation is handled\n    }\n}\n\n// Attach the event handler to the DataGridView\nprivate void Form1_Load(object sender, EventArgs e)\n{\n    dataGridView1.SortCompare += new DataGridViewSortCompareEventHandler(dataGridView1_SortCompare);\n}