public struct Gauss {\n    public double mean;\n    public double variance;\n\n    public Gauss(double mean, double variance) {\n        this.mean = mean;\n        this.variance = variance;\n    }\n\n    // Multiplication by a scalar\n    public static Gauss operator * (Gauss g, double d) {\n        return new Gauss(g.mean * d, g.variance * d * d);\n    }\n\n    // Addition of two Gauss distributions\n    public static Gauss operator + (Gauss a, Gauss b) {\n        double mean = a.mean + b.mean;\n        double variance = a.variance + b.variance;\n        return new Gauss(mean, variance);\n    }\n}