using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        ProcessStartInfo startInfo = new ProcessStartInfo();\n        startInfo.FileName = "yourExecutable.exe"; // Specify the executable name\n        startInfo.Arguments = "yourArguments"; // Specify any arguments if needed\n        startInfo.WorkingDirectory = "yourWorkingDirectory"; // Specify the working directory if needed\n        startInfo.UseShellExecute = false; // Set to false to redirect input/output\n\n        try\n        {\n            using (Process exeProcess = Process.Start(startInfo))\n            {\n                // Optionally wait for the process to exit\n                exeProcess.WaitForExit();\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}