using System;\nusing Microsoft.VisualStudio.TestTools.UITesting;\nusing Microsoft.VisualStudio.TestTools.UnitTesting;\n\nnamespace AjaxTest\n{\n    [CodedUITest]\n    public class AjaxUITest\n    {\n        [TestMethod]\n        public void TestMethod1()\n        {\n            BrowserWindow browser = BrowserWindow.Launch(new Uri("http://your-web-page-url"));\n            \n            // Perform some actions that trigger AJAX\n            // e.g., clicking a button\n            var button = new HtmlButton(browser);\n            button.SearchProperties[HtmlButton.PropertyNames.Id] = "yourButtonId";\n            Mouse.Click(button);\n            \n            // Wait for AJAX to complete\n            Playback.Wait(5000); // Adjust the wait time as necessary\n            \n            // Perform validations after AJAX call completes\n            var resultElement = new HtmlDiv(browser);\n            resultElement.SearchProperties[HtmlDiv.PropertyNames.Id] = "resultDivId";\n            Assert.IsTrue(resultElement.Exists, "Result element does not exist.");\n        }\n    }\n}