using System;\nusing System.IO;\nusing System.Reflection;\n\npublic class Program\n{\n    static void Main(string[] args)\n    {\n        AppDomain.CurrentDomain.AssemblyResolve += new ResolveEventHandler(LoadFromEmbeddedResources);\n        \n        // Your code that uses the third-party DLL\n        // Example:\n        // var instance = new ThirdPartyClass();\n        // instance.SomeMethod();\n    }\n\n    private static Assembly LoadFromEmbeddedResources(object sender, ResolveEventArgs args)\n    {\n        string resourceName = new AssemblyName(args.Name).Name + ".dll";\n        string resourceFullName = "YourNamespace." + resourceName;\n\n        using (Stream stream = Assembly.GetExecutingAssembly().GetManifestResourceStream(resourceFullName))\n        {\n            if (stream == null)\n                return null;\n\n            byte[] assemblyData = new byte[stream.Length];\n            stream.Read(assemblyData, 0, assemblyData.Length);\n            return Assembly.Load(assemblyData);\n        }\n    }\n}