using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Text;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string filePath = "path_to_your_large_file.txt";\n        int numberOfLines = 10;\n        IEnumerable<string> lastLines = GetLastLines(filePath, numberOfLines);\n\n        foreach (string line in lastLines)\n        {\n            Console.WriteLine(line);\n        }\n    }\n\n    static IEnumerable<string> GetLastLines(string filePath, int numberOfLines)\n    {\n        List<string> lines = new List<string>();\n        const int bufferSize = 1024;\n        byte[] buffer = new byte[bufferSize];\n        long fileLength;\n        int bytesRead;\n        int lineCount = 0;\n        StringBuilder lineBuffer = new StringBuilder();\n\n        using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n        {\n            fileLength = fs.Length;\n            long position = fileLength;\n\n            while (position > 0 && lineCount < numberOfLines)\n            {\n                position = Math.Max(position - bufferSize, 0);\n                fs.Seek(position, SeekOrigin.Begin);\n                bytesRead = fs.Read(buffer, 0, (int)(fileLength - position));\n\n                for (int i = bytesRead - 1; i >= 0; i--)\n                {\n                    if (buffer[i] == '\n')\n                    {\n                        if (lineBuffer.Length > 0)\n                        {\n                            lines.Insert(0, lineBuffer.ToString());\n                            lineBuffer.Clear();\n                            lineCount++;\n\n                            if (lineCount == numberOfLines)\n                            {\n                                break;\n                            }\n                        }\n                    }\n                    else\n                    {\n                        lineBuffer.Insert(0, (char)buffer[i]);\n                    }\n                }\n\n                fileLength = position;\n            }\n\n            if (lineBuffer.Length > 0 && lineCount < numberOfLines)\n            {\n                lines.Insert(0, lineBuffer.ToString());\n            }\n        }\n\n        return lines;\n    }\n}