using System;\nusing RRDSharp;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new RRD database\n        string rrdFilePath = "test.rrd";\n        RrdDb rrdDb = new RrdDb(rrdFilePath, new DateTime(2023, 1, 1), 300);\n        rrdDb.DefDatasource("temperature", DsTypes.GAUGE, 600, double.NaN, double.NaN);\n        rrdDb.DefArchive(ConsolFun.AVERAGE, 0.5, 1, 24);\n        rrdDb.Close();\n\n        // Update the RRD database with some data\n        rrdDb = new RrdDb(rrdFilePath);\n        Sample sample = rrdDb.CreateSample();\n        sample.Time = DateTime.Now;\n        sample.SetValue("temperature", 23.5);\n        sample.Update();\n        rrdDb.Close();\n\n        // Fetch data from the RRD database\n        rrdDb = new RrdDb(rrdFilePath);\n        FetchRequest fetchRequest = rrdDb.CreateFetchRequest(ConsolFun.AVERAGE, new DateTime(2023, 1, 1), DateTime.Now);\n        FetchData fetchData = fetchRequest.FetchData();\n        Console.WriteLine("Fetched Data:");\n        foreach (var time in fetchData.Timestamps)\n        {\n            Console.WriteLine($"Time: {time}, Value: {fetchData.GetValues("temperature")[time]}");\n        }\n        rrdDb.Close();\n    }\n}