// In the ASPX page code-behind\npublic partial class MyPage : System.Web.UI.Page\n{\n    public event EventHandler UserLoggedIn;\n\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Assuming you have a login control that triggers this event\n        LoginControl.UserLoggedIn += OnUserLoggedIn;\n    }\n\n    protected void OnUserLoggedIn(object sender, EventArgs e)\n    {\n        // Raise the event to notify subscribed user controls\n        UserLoggedIn?.Invoke(this, EventArgs.Empty);\n    }\n}