using System;\nusing System.IO;\nusing System.Text;\n\npublic class FileHandler\n{\n    public void WriteAnsiTextToFile(string filePath, string content)\n    {\n        // Specify the encoding as ANSI\n        Encoding ansiEncoding = Encoding.GetEncoding(1252); // ANSI code page for Western European (Windows)\n\n        using (StreamWriter writer = new StreamWriter(filePath, false, ansiEncoding))\n        {\n            writer.Write(content);\n        }\n    }\n}