using Microsoft.TeamFoundation.Client;\nusing Microsoft.TeamFoundation.VersionControl.Client;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Connect to the TFS server\n        Uri tfsUri = new Uri("http://your-tfs-server:8080/tfs/YourCollection");\n        TfsTeamProjectCollection tpc = new TfsTeamProjectCollection(tfsUri);\n        tpc.EnsureAuthenticated();\n\n        // Get the version control server\n        VersionControlServer versionControl = tpc.GetService<VersionControlServer>();\n\n        // Specify the path to the project or folder you want to check\n        string projectPath = "$/YourProjectPath";\n\n        // Get the history of the specified path\n        IEnumerable changesets = versionControl.QueryHistory(\n            projectPath,\n            VersionSpec.Latest,\n            0,\n            RecursionType.Full,\n            null,\n            null,\n            null,\n            int.MaxValue,\n            true,\n            false\n        );\n\n        // Iterate through the changesets to find deleted items\n        foreach (Changeset changeset in changesets)\n        {\n            foreach (Change change in changeset.Changes)\n            {\n                if (change.ChangeType.HasFlag(ChangeType.Delete))\n                {\n                    Console.WriteLine($"Deleted file: {change.Item.ServerItem} in changeset {changeset.ChangesetId}");\n                }\n            }\n        }\n    }\n}