var conditionalChecks = from c in db.ConditionalProperties\n                        join p in db.Properties on c.PropertyId equals p.Id\n                        where c.IsChecked == 1 && (p.SystemName == "eatable" || p.SystemName == "diy")\n                        select c.ItemId;\n\nvar conditionalCount = from c in conditionalChecks\n                       group c by c into g\n                       select new\n                       {\n                           ItemId = g.Key,\n                           NumTrue = g.Count()\n                       };\n\nvar itemResult = from c in conditionalCount\n                 join i in db.Items on c.ItemId equals i.Id\n                 select new\n                 {\n                     i,\n                     c.NumTrue\n                 };\n\nvar finalResult = from r in itemResult\n                  where r.NumTrue == 2\n                  select r.i;\n\nvar finalList = finalResult.ToList();