using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        const int size = 6000000;\n        int[] array = new int[size];\n        List<int> list = new List<int>(size);\n\n        for (int i = 0; i < size; i++)\n        {\n            array[i] = i;\n            list.Add(i);\n        }\n\n        Stopwatch stopwatch = new Stopwatch();\n\n        // Benchmark array iteration\n        stopwatch.Start();\n        long arraySum = 0;\n        for (int i = 0; i < size; i++)\n        {\n            arraySum += array[i];\n        }\n        stopwatch.Stop();\n        Console.WriteLine($"Array iteration time: {stopwatch.ElapsedMilliseconds} ms");\n\n        // Benchmark list iteration\n        stopwatch.Restart();\n        long listSum = 0;\n        for (int i = 0; i < size; i++)\n        {\n            listSum += list[i];\n        }\n        stopwatch.Stop();\n        Console.WriteLine($"List iteration time: {stopwatch.ElapsedMilliseconds} ms");\n    }\n}