public void saveToFile()\n{\n    FileInfo t = new FileInfo("Workout Log.txt");\n    using (StreamWriter Tex = t.CreateText())\n    {\n        Tex.WriteLine("---------------Workout Buddy Log---------------");\n        Tex.WriteLine("------------------- " + DateTime.Now.ToShortDateString() + " ------------------");\n        Tex.Write(Tex.NewLine);\n\n        foreach (Control ctrl in this.Controls)\n        {\n            if (ctrl is ComboBox && ctrl.Name.StartsWith("dayBox"))\n            {\n                ComboBox dayBox = (ComboBox)ctrl;\n                if (dayBox.Text != "Pick One")\n                {\n                    Tex.WriteLine("Day: " + dayBox.Text);\n                }\n                else\n                {\n                    Tex.WriteLine("Day: N/A");\n                }\n            }\n        }\n    }\n}