using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the absolute paths\n        string toolPath = Path.GetFullPath(Path.Combine(Directory.GetCurrentDirectory(), @"../Tools/RunHidden"));\n        string scriptPath = Path.GetFullPath(Path.Combine(Directory.GetCurrentDirectory(), @"../My-Bridge.bat"));\n\n        // Start the process\n        Process bridge_process = new Process();\n        bridge_process.StartInfo.FileName = toolPath;\n        bridge_process.StartInfo.Arguments = scriptPath;\n        bridge_process.Start();\n\n        // Optional: Wait for the process to exit\n        bridge_process.WaitForExit();\n    }\n}