using System;\n     using Twilio;\n     using Twilio.Rest.Api.V2010.Account;\n\n     public class SmsService\n     {\n         private const string accountSid = "your_account_sid";\n         private const string authToken = "your_auth_token";\n\n         public SmsService()\n         {\n             TwilioClient.Init(accountSid, authToken);\n         }\n\n         public void SendSms(string to, string message)\n         {\n             var messageOptions = new CreateMessageOptions(\n                 new Twilio.Types.PhoneNumber(to))\n             {\n                 Body = message\n             };\n\n             var msg = MessageResource.Create(messageOptions);\n             Console.WriteLine($"Message sent: {msg.Sid}");\n         }\n     }