// Assuming you have a DataSet with a DataTable\nDataSet dataSet = GetDataSetFromDatabase(); // Your method to fetch DataSet\nDataTable dataTable = dataSet.Tables[0]; // Accessing the first DataTable\n\n// Sorting the DataTable on a specific column\ndataTable.DefaultView.Sort = "YourColumnName ASC"; // Use "DESC" for descending order\n\n// After sorting, set the sorted DefaultView as the DataSource of your grid control\nyourGridControl.DataSource = dataTable.DefaultView;\nyourGridControl.DataBind();