protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if (FileUpload1.HasFiles)\n    {\n        var files = FileUpload1.PostedFiles;\n        string uploadFolderPath = Server.MapPath("~/Uploads/");\n        \n        foreach (var file in files)\n        {\n            // Generate a dynamic file name (e.g., using a GUID)\n            string fileName = Guid.NewGuid().ToString() + Path.GetExtension(file.FileName);\n            string filePath = Path.Combine(uploadFolderPath, fileName);\n\n            // Save the file to the server\n            file.SaveAs(filePath);\n        }\n\n        UploadStatus.Text = $"{files.Count} files have been uploaded successfully.";\n    }\n    else\n    {\n        UploadStatus.Text = "Please select files to upload.";\n    }\n}