using System;\nusing System.IO;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "path_to_your_image_file.jpg";\n        bool fileInUse = IsFileInUse(filePath);\n\n        if (!fileInUse)\n        {\n            Console.WriteLine("File is not in use, you can proceed.");\n            // Your file processing logic here\n        }\n        else\n        {\n            Console.WriteLine("File is in use, please wait.");\n        }\n    }\n\n    static bool IsFileInUse(string filePath)\n    {\n        try\n        {\n            // Attempt to open the file exclusively\n            using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read, FileShare.None))\n            {\n                // If we get here, the file is not in use\n                return false;\n            }\n        }\n        catch (IOException)\n        {\n            // If an IOException is thrown, the file is in use\n            return true;\n        }\n    }\n}