using System;\n    using System.Data.Common;\n\n    class Program\n    {\n        static void Main()\n        {\n            string connectionStringName = "SqlServerConnection"; // or "OracleConnection"\n            DatabaseHelper dbHelper = new DatabaseHelper(connectionStringName);\n\n            using (DbConnection connection = dbHelper.GetConnection())\n            {\n                connection.Open();\n                DbCommand command = connection.CreateCommand();\n                command.CommandText = "SELECT * FROM YourTable";\n\n                using (DbDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        // Process each row\n                        Console.WriteLine(reader[0].ToString());\n                    }\n                }\n            }\n        }\n    }