public DataSet ExecuteQuery(DateTime registrationDeadline)\n{\n    DataSet ds = null;\n    string sql = @"\n        SELECT DISTINCT \n            ResponseFormParent.ResponseFormParentId, \n            ResponseFormParent.YourIntColumn1, \n            ResponseFormParent.YourIntColumn2, \n            ResponseFormParent.YourIntColumn3, \n            ResponseFormParent.YourDateTimeColumn, \n            ResponseFormParent.YourNVarCharColumn\n        FROM ResponseFormParent \n        INNER JOIN ResponseForm \n            ON ResponseFormParent.ResponseFormParentId = ResponseForm.ResponseFormParentId \n        WHERE ResponseForm.RegistrationDeadline >= @RegistrationDeadline";\n\n    try\n    {\n        using (SqlConnection dbconn = new SqlConnection(connectionString))\n        using (SqlCommand dbcmd = new SqlCommand(sql, dbconn))\n        {\n            dbcmd.Parameters.AddWithValue("@RegistrationDeadline", registrationDeadline);\n            SqlDataAdapter dbadapter = new SqlDataAdapter(dbcmd);\n            ds = new DataSet();\n            dbadapter.Fill(ds);\n        }\n    }\n    catch (Exception ex)\n    {\n        Utility.LogError(sql, ex);\n    }\n    return ds;\n}