using System;\nusing System.Data.Odbc;\n\npublic class InsertClobExample\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string clobData = new string('A', 5000); // Example CLOB data with more than 4000 characters\n\n        using (OdbcConnection connection = new OdbcConnection(connectionString))\n        {\n            connection.Open();\n            \n            string sql = "INSERT INTO YourTable (ClobColumn) VALUES (?)";\n            \n            using (OdbcCommand command = new OdbcCommand(sql, connection))\n            {\n                OdbcParameter param = new OdbcParameter();\n                param.OdbcType = OdbcType.Text;\n                param.Value = clobData;\n                command.Parameters.Add(param);\n\n                int rowsAffected = command.ExecuteNonQuery();\n                Console.WriteLine($"{rowsAffected} row(s) inserted.");\n            }\n        }\n    }\n}