private void TextBlock_MouseDown(object sender, System.Windows.Input.MouseButtonEventArgs e)\n   {\n       // Make the WindowsFormsHost visible if it is hidden\n       if (windowsFormsHost.Visibility == Visibility.Hidden)\n       {\n           windowsFormsHost.Visibility = Visibility.Visible;\n       }\n\n       // Create a DoubleAnimation to fade in the WindowsFormsHost\n       DoubleAnimation fadeInAnimation = new DoubleAnimation\n       {\n           From = 0,\n           To = 1,\n           Duration = new Duration(TimeSpan.FromSeconds(1))\n       };\n\n       // Apply the animation to the WindowsFormsHost's opacity\n       windowsFormsHost.BeginAnimation(UIElement.OpacityProperty, fadeInAnimation);\n   }