public class Consumer\n{\n    private List<IRandomNumberGenerator> generators;\n    private List<double> seeds;\n\n    [ImportingConstructor]\n    public Consumer(Func<double, IRandomNumberGenerator> generatorFactory)\n    {\n        generators = new List<IRandomNumberGenerator>();\n        seeds = new List<double> { 1.0, 2.0, 3.0 };\n\n        foreach (var seed in seeds)\n        {\n            generators.Add(generatorFactory(seed));\n        }\n    }\n}