using iTextSharp.text.pdf;\n     using System.IO;\n\n     public void FillPdfForm(string sourcePdfPath, string destinationPdfPath)\n     {\n         using (var reader = new PdfReader(sourcePdfPath))\n         using (var stamper = new PdfStamper(reader, new FileStream(destinationPdfPath, FileMode.Create)))\n         {\n             var formFields = stamper.AcroFields;\n             formFields.SetField("fieldName1", "value1");\n             formFields.SetField("fieldName2", "value2");\n             stamper.FormFlattening = true; // Optional: Make fields non-editable\n         }\n     }