using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class AesEncryption\n{\n    public static byte[] Encrypt(string plainText, byte[] key, byte[] iv)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = key;\n            aes.IV = iv;\n\n            ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n\n            using (MemoryStream ms = new MemoryStream())\n            {\n                using (CryptoStream cs = new CryptoStream(ms, encryptor, CryptoStreamMode.Write))\n                {\n                    using (StreamWriter sw = new StreamWriter(cs))\n                    {\n                        sw.Write(plainText);\n                    }\n                }\n                return ms.ToArray();\n            }\n        }\n    }\n\n    public static string Decrypt(byte[] cipherText, byte[] key, byte[] iv)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = key;\n            aes.IV = iv;\n\n            ICryptoTransform decryptor = aes.CreateDecryptor(aes.Key, aes.IV);\n\n            using (MemoryStream ms = new MemoryStream(cipherText))\n            {\n                using (CryptoStream cs = new CryptoStream(ms, decryptor, CryptoStreamMode.Read))\n                {\n                    using (StreamReader sr = new StreamReader(cs))\n                    {\n                        return sr.ReadToEnd();\n                    }\n                }\n            }\n        }\n    }\n}\n\n// Example usage\npublic class Example\n{\n    public static void Main()\n    {\n        string original = "Sensitive data to encrypt";\n        byte[] key = Encoding.UTF8.GetBytes("12345678901234567890123456789012"); // 32 bytes for AES-256\n        byte[] iv = Encoding.UTF8.GetBytes("1234567890123456"); // 16 bytes for AES\n\n        byte[] encrypted = AesEncryption.Encrypt(original, key, iv);\n        string decrypted = AesEncryption.Decrypt(encrypted, key, iv);\n\n        Console.WriteLine($"Original: {original}");\n        Console.WriteLine($"Decrypted: {decrypted}");\n    }\n}