using System;\nusing System.Security.Cryptography;\n\nnamespace RSAKeyGeneration\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Specify the key size (e.g., 2048 bits)\n            int keySize = 2048;\n\n            using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider(keySize))\n            {\n                // Export the public key\n                string publicKey = rsa.ToXmlString(false);\n                Console.WriteLine("Public Key: \n" + publicKey);\n\n                // Export the private key\n                string privateKey = rsa.ToXmlString(true);\n                Console.WriteLine("Private Key: \n" + privateKey);\n\n                // Optionally, save the keys to a file or secure storage\n                // System.IO.File.WriteAllText("publicKey.xml", publicKey);\n                // System.IO.File.WriteAllText("privateKey.xml", privateKey);\n            }\n        }\n    }\n}