using System;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string s = "abcdefghab";\n        var repeatedChars = s.GroupBy(c => c)\n                             .Where(g => g.Count() > 1)\n                             .Select(g => g.Key)\n                             .ToArray();\n\n        Console.WriteLine(new string(repeatedChars)); // Output: ab\n    }\n}