XmlNamespaceManager nsMgr = new XmlNamespaceManager(xmlDoc.NameTable);\nnsMgr.AddNamespace("tns", "http://www.w3.org/2005/Atom");\nnsMgr.AddNamespace("thr", "http://purl.org/syndication/thread/1.0");\nnsMgr.AddNamespace("creativeCommons", "http://backend.userland.com/creativeCommonsRssModule");\n\nXmlNodeList selectedNodes = xmlDoc.SelectNodes("/tns:feed/tns:entry", nsMgr);\nforeach (XmlNode selectedNode in selectedNodes)\n{\n    string id = selectedNode.SelectSingleNode("tns:id", nsMgr)?.InnerText;\n    string date = selectedNode.SelectSingleNode("tns:updated", nsMgr)?.InnerText;\n    string title = selectedNode.SelectSingleNode("tns:title", nsMgr)?.InnerText;\n    string link = selectedNode.SelectSingleNode("tns:link[@rel='alternate']", nsMgr)?.Attributes["href"]?.Value;\n\n    // Process the extracted values\n    Console.WriteLine($"ID: {id}, Date: {date}, Title: {title}, Link: {link}");\n}