using System;\nusing System.Collections.Concurrent;\nusing System.Web;\n\npublic class Global : HttpApplication\n{\n    private static ConcurrentDictionary<string, DateTime> activeSessions = new ConcurrentDictionary<string, DateTime>();\n\n    protected void Session_Start(Object sender, EventArgs e)\n    {\n        activeSessions.TryAdd(Session.SessionID, DateTime.Now);\n    }\n\n    protected void Session_End(Object sender, EventArgs e)\n    {\n        DateTime removedValue;\n        activeSessions.TryRemove(Session.SessionID, out removedValue);\n    }\n\n    public static ConcurrentDictionary<string, DateTime> GetActiveSessions()\n    {\n        return new ConcurrentDictionary<string, DateTime>(activeSessions);\n    }\n}