using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\nusing Newtonsoft.Json;\n\nclass JsonRpcClient\n{\n    private readonly string _url;\n\n    public JsonRpcClient(string url)\n    {\n        _url = url;\n    }\n\n    public T Call<T>(string method, params object[] parameters)\n    {\n        var request = WebRequest.Create(_url);\n        request.ContentType = "application/json";\n        request.Method = "POST";\n\n        var rpcRequest = new\n        {\n            jsonrpc = "2.0",\n            method = method,\n            @params = parameters,\n            id = 1\n        };\n\n        string json = JsonConvert.SerializeObject(rpcRequest);\n\n        using (var streamWriter = new StreamWriter(request.GetRequestStream()))\n        {\n            streamWriter.Write(json);\n            streamWriter.Flush();\n            streamWriter.Close();\n        }\n\n        var response = (HttpWebResponse)request.GetResponse();\n        using (var streamReader = new StreamReader(response.GetResponseStream()))\n        {\n            var result = streamReader.ReadToEnd();\n            var rpcResponse = JsonConvert.DeserializeObject<JsonRpcResponse<T>>(result);\n            return rpcResponse.Result;\n        }\n    }\n\n    private class JsonRpcResponse<T>\n    {\n        public string Jsonrpc { get; set; }\n        public T Result { get; set; }\n        public int Id { get; set; }\n        public object Error { get; set; }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var client = new JsonRpcClient("http://example.com/jsonrpc");\n        var result = client.Call<string>("methodName", "param1", 42);\n        Console.WriteLine("Result: " + result);\n    }\n}