using System;\nusing HtmlAgilityPack;\n\npublic class HtmlValidator\n{\n    public bool IsHtmlValid(string html)\n    {\n        try\n        {\n            var doc = new HtmlDocument();\n            doc.LoadHtml(html);\n\n            // Check for parsing errors\n            if (doc.ParseErrors != null && doc.ParseErrors.Any())\n            {\n                return false;\n            }\n\n            return true;\n        }\n        catch (Exception)\n        {\n            // If an exception occurs, the HTML is not valid\n            return false;\n        }\n    }\n}\n\n// Usage\npublic class Program\n{\n    public static void Main()\n    {\n        HtmlValidator validator = new HtmlValidator();\n        string userInputHtml = "<div><p>Sample text</div>"; // Example of invalid HTML\n\n        bool isValid = validator.IsHtmlValid(userInputHtml);\n        Console.WriteLine(isValid ? "HTML is valid." : "HTML is not valid.");\n    }\n}