using System;\nusing System.IO.MemoryMappedFiles;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a memory-mapped file\n        using (var mmf = MemoryMappedFile.CreateNew("testmap", 10000))\n        {\n            // Create a view accessor for writing\n            using (var accessor = mmf.CreateViewAccessor())\n            {\n                string message = "Hello, Service!";\n                byte[] messageBytes = Encoding.UTF8.GetBytes(message);\n\n                // Write to the memory-mapped file\n                accessor.Write(0, messageBytes.Length);\n                accessor.WriteArray(4, messageBytes, 0, messageBytes.Length);\n            }\n\n            // Another process can read from this memory-mapped file\n            // using MemoryMappedFile.OpenExisting("testmap")\n        }\n    }\n}