private DataTable GetDeviceData(string serialNumber)\n{\n    DataTable dtDevice = new DataTable();\n    using (SqlConnection conn = new SqlConnection("your_connection_string"))\n    {\n        string query = "SELECT DeviceSerialNumber, BuildID, LEMSCredentialsID, LEMSSoftwareID, OwnerID, RestagedDate " +\n                       "FROM tblDevice WHERE DeviceSerialNumber = @SerialNumber";\n        using (SqlCommand cmd = new SqlCommand(query, conn))\n        {\n            cmd.Parameters.AddWithValue("@SerialNumber", serialNumber);\n            using (SqlDataAdapter da = new SqlDataAdapter(cmd))\n            {\n                // Disable constraints before filling the DataTable\n                da.FillSchema(dtDevice, SchemaType.Source);\n                dtDevice.Constraints.Clear();\n                da.Fill(dtDevice);\n            }\n        }\n    }\n    return dtDevice;\n}\n\n// Usage\nprivate void LoadDeviceData()\n{\n    var dtDevice = GetDeviceData(txtDeviceSerial.Text);\n    // Use dtDevice as needed\n}